Pessoa::Pessoa(string nomesMasc[], string nomesFem[], string natBr[], string natG[], string fotosMasc[], string fotosFem[]){
  
  //quantidade de diferentes pessoas atualmente implementadas
  int quant = 5;
  //pessoa vai ser homem ou mulher?
  sexo = rand() % 2;
  //nome no RG estará certo ou errado?
  int num = rand() % 100;   
  //nome certo
  if(num < 90){
    //escolhe um nome do mesmo sexo da pessoa
    num = rand() % quant;
    //se for mulher
    if(sexo){
      nomeRg = nomesFem[num];
    }
    //se for homem
    else {
      nomeRg = nomesMasc[num];
    }
    nomeEr = false;
  }
  else{
    //escolhe um nome do outro sexo da pessoa
    num = rand() % quant;
    //se for mulher
    if(sexo){
      nomeRg = nomesMasc[num];
    }
    //se for homem
    else {
      nomeRg = nomesFem[num];
    }
    nomeEr = true;
  }
  //nome da mae estará certo?
  num = rand() % 100;
  //nome certo
  if(num < 90){
    num = rand() % quant;
    maeRg = nomesFem[num];
    maeEr = false;
  }
  else {
    //nome errado
    num = rand() % quant;
    maeRg = nomesMasc[num];
    maeEr = true;
  }
  //nome do pai estará certo?
  num = rand() % 100;
  //nome certo
  if(num < 90){
    num = rand() % quant;
    paiRg = nomesMasc[num];
    paiEr = false;
  }
  else {
    //nome errado
    num = rand() % quant;
    paiRg = nomesFem[num];
    paiEr = true;
  }
  //naturalidade estará correta?
  num = rand() % 100;
  //naturalidade certa
  if(num < 90){
    num = rand() % quant;
    natRg = natBr[num];
    natEr = false;
  }
  //naturalidade errada
  else {
    num = rand() % quant;
    natRg = natG[num];
    natEr = true;
  }
  //atribuindo uma data aleatória
  dia = rand() % 31 + 1;
  mes = rand() % 12 + 1;
  ano = rand() % 200 + 1900;
  //verificando se essa data de nascimento é coerente
  nascVal(ano, mes, dia);
}
Pessoa::Pessoa(){};
//construtor de cópia
Pessoa::Pessoa(const Pessoa &copiado){
  sexo = copiado.sexo;
  nomeRg = copiado.nomeRg;
  paiRg = copiado.paiRg;
  maeRg = copiado.maeRg;
  natRg = copiado.natRg;
  dia = copiado.dia;
  mes = copiado.mes;
  ano = copiado.ano;
  nomeEr = copiado.nomeEr;
  dataEr = copiado.dataEr;
  nascEr = copiado.nascEr;
  natEr = copiado.natEr;
}
//sobrecarga de =
const Pessoa &Pessoa::operator=(const Pessoa &copiado){
  if(&copiado != this){
    sexo = copiado.sexo;
    nomeRg = copiado.nomeRg;
    paiRg = copiado.paiRg;
    maeRg = copiado.maeRg;
    natRg = copiado.natRg;
    dia = copiado.dia;
    mes = copiado.mes;
    ano = copiado.ano;
    nomeEr = copiado.nomeEr;
    dataEr = copiado.dataEr;
    nascEr = copiado.nascEr;
    natEr = copiado.natEr;
  }
  return *this;
}

bool Pessoa::dataVal(int ano,int mes, int dia){
//Verificação do limite máximo de "dia" de acordo com o mes.
      if(mes == 2){
          //Verificação de ano bissexto apenas em fevereiro.
          if (((ano % 4 == 0) && (ano % 100 != 0)) || (ano % 400 == 0))
              if(dia > 0 && dia <= 29)
                return true;
              else 
                return false;
          else
              if(dia > 0 && dia <= 28)
                return true; 
              else 
                return false;
      }   
      else{
          if(mes == 4 || mes == 6 || mes == 9 || mes == 11)
              if(dia > 0 && dia <= 30)
                return true;
              else 
                return false;
          else
              if(dia > 0 && dia <= 31)
                return true; 
              else 
                return false;
      }
      //Verificação das demais informações.
      if(mes > 0 && mes <= 12)
        return true; 
      else
        return false;

}
void Pessoa::nascVal(int ano, int mes, int dia){
  if(dataVal(ano, mes, dia)){
    dataEr = false;
    int idade = 2022 - ano;
    if(18 <= idade && idade <= 110)
      nascEr = false;
    else
      nascEr = true;
  }
  else{
    dataEr = true;
    nascEr = true;
  }
}
